    checkStudentAge: function checkStudentAge() {
      if (this.inputStudentDateOfBirth === '' || this.inputStudentDateOfBirth === null) {
        this.disableIC = true;
        this.inputStudentIC = '';
      } else {
        //convert dd/mm/yyyy to mm/dd/yyyy for convert into new date to calculate age
        var splittedDate = this.inputStudentDateOfBirth.split('/');
        var bDay = [splittedDate[1], splittedDate[0], splittedDate[2]].join('/'); //calculate age

        var today = new Date();
        var birthDate = new Date(bDay);
        var age = today.getFullYear() - birthDate.getFullYear();
        var m = today.getMonth() - birthDate.getMonth();

        if (m < 0 || m === 0 && today.getDate() < birthDate.getDate()) {
          age--;
        }

        if (age >= 16) {
          this.disableIC = false;
        } else {
          this.disableIC = true;
          this.inputStudentIC = '';
        }
      }
    },
    EnrolledinOtherCenterShow: function EnrolledinOtherCenterShow() {
      if (this.ddlStudentEnrolledinOtherCenter === 'No') {
        this.divEnrolledinOtherCenterShow = false;
        this.inputStudentEnrolledCenterName = '';
        this.ddlStudentEnrolledCenterType = '';
      } else {
        this.divEnrolledinOtherCenterShow = true;
      }
    },
    studentPageVueTourStart: function studentPageVueTourStart() {
